package com.sfxcode.sapphire.core.demo.tutorial.controller

import com.sfxcode.sapphire.core.demo.tutorial.ApplicationController
import javafx.event.ActionEvent
import javafx.scene.control.Button

class NavigationController extends AbstractViewController {

  def actionClickButton(event: ActionEvent) {
    logger.debug(event.toString)

    // resolve controller by getViewController lookup
    val controller = getViewController[StatusBarController]()
    controller.foreach(c => {
      val button = event.getSource.asInstanceOf[Button]
      c.updateLabel(button)
    })
  }

  def actionToggleWorkspace(event: ActionEvent) {
    actionClickButton(event)
    val actualController = workspaceManager.actualController
    val barChartController = mainWindowController.barChartController
    val workspaceController = mainWindowController.workspaceController
    if (actualController == workspaceController) {
      workspaceManager.updatePaneContent(barChartController)
    } else
      workspaceManager.updatePaneContent(workspaceController)
  }

  def actionShowPersonController(event: ActionEvent): Unit = {
    actionClickButton(event)
    val personController = mainWindowController.personController
    workspaceManager.updatePaneContent(personController)
  }

  def actionReload(event: ActionEvent): Unit = {
    actionClickButton(event)
    getBean[ApplicationController]().reload()
  }

}

case class Author(name: String)
case class Book(id: Long, title: String, pages: Int, author: Author)

// create FXBean for sample case class
val scalaBook = Book(1, "Programming In Scala", 852, Author("Martin Odersky"))
val book = FXBean[Book](scalaBook)

// getValue and updateValue are used for bean property access and modification
// getProperty, getStringProperty ... 
// are used for automatic create a JavaFX Property
val title = book.getValue("title")
// "Programming In Scala"

val titleProperty = book.getStringProperty("title")

book.updateValue("title", "Programming In Scala 3.0")
// title is updated, titleProperty as well

val newTitle = book.getValue("title")
// "Programming In Scala 3.0"

val newTitleFromProperty = titleProperty.getValue  // "Programming In Scala 3.0"

// getValue and updateValue for underlying class by dot syntax
val authorName = book.getValue("author.name")  // "Martin Odersky"

ViewController extends FxmlLoading trait. FxmlLoading provide the
getController[T <: ViewController](fxml: String = "")
method. The package path of the controller is the default fxml directory. [name]Controller is converted to [name].fxml (WorkspaceController -> Workspace.fxml).
package controller

class MainWindowController extends ViewController with LazyLogging {

  // workspaces
  lazy val workspaceController = getController[WorkspaceController]()
  // navigation
  lazy val defaultNavigationController = 
     getController[DefaultNavigationController]()

}
WorkspaceController fxml must be saved at /controller/Workspace.fxml.

lazy val controller = 
     getController[WorkspaceController]("/fxml/special/path/special_name.fxml")
